mapper.select:
select
id, love_title, love_explain, love_need_money, love_now_money, love_cover, love_state, create_time, update_time
from jiaoyou.love_heart_donation


        and id = #{id}


        and love_title = #{loveTitle}


        and love_explain = #{loveExplain}


        and love_need_money = #{loveNeedMoney}


        and love_now_money = #{loveNowMoney}


        and love_cover = #{loveCover}


        and love_state = #{loveState}


        and create_time = #{createTime}


        and update_time = #{updateTime}
