mapper.select:
select
id, user_id, order_number, order_status, order_shop_name, order_shop_id, order_price, order_shop_specs,
order_real_price, order_shop_number, recever_people_name, recever_phone, recever_address, order_del_status,
create_time, update_time
from jiaoyou.order_manage


        and id = #{id}


        and user_id = #{userId}


        and order_number = #{orderNumber}


        and order_status = #{orderStatus}


        and order_shop_name = #{orderShopName}


        and order_shop_id = #{orderShopId}


        and order_price = #{orderPrice}


        and order_shop_specs = #{orderShopSpecs}


        and order_real_price = #{orderRealPrice}


        and order_shop_number = #{orderShopNumber}


        and recever_people_name = #{receverPeopleName}


        and recever_phone = #{receverPhone}


        and recever_address = #{receverAddress}


        and order_del_status = #{orderDelStatus}


        and create_time = #{createTime}


        and update_time = #{updateTime}
