mapper.select:
select
id, server_title, server_sort, server_status, create_time, update_time
from jiaoyou.server_info_type


        and id = #{id}


        and server_title = #{serverTitle}


        and server_sort = #{serverSort}


        and server_status = #{serverStatus}


        and create_time = #{createTime}


        and update_time = #{updateTime}
